import React from 'react';
import { Container, Section, Button } from 'react-bulma-components';
import axios from 'axios';

const enviarCorreo = () => {
    const handleSendEmail = async () => {
        const apiKey = 're_JL4b2MHx_35UpwjAyJsSogNyWaZE3tmmU';  // Reemplaza con tu clave de API de Resend
        const to = 'recipient@example.com'; // Reemplaza con el correo del destinatario
        const from = 'jo.almontes@duocuc.cl'; // Reemplaza con tu correo
        const subject = 'Hello from Resend!';
        const text = 'This is a test email sent from Resend.';

        try {
            const response = await axios.post('https://api.resend.com/send', {
                from,
                to,
                subject,
                text
            }, {
                headers: {
                    'Authorization': `Bearer ${apiKey}`,
                    'Content-Type': 'application/json'
                }
            });

            console.log('Email sent successfully:', response.data);
        } catch (error) {
            console.error('Error sending email:', error);
        }
    };

    return (
        <Section>
            <Container>
                <div className='is-pulled-right'> 
                    <Button onClick={handleSendEmail} color="primary">
                        Enviar Correo
                    </Button>
                </div>
            </Container>
        </Section>
    );
};

export default enviarCorreo;
