import React, { useState, useEffect } from 'react';
import { Card, Columns, Content, Heading, Modal } from 'react-bulma-components';
import { deleteProduct, updateProduct, getProducts } from '../sevices';
import DeleteButton from './Eliminar.js';
import EditarButton from './EditarButton.js';
import Form from "./Form";

const ListProduct = ({ products }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [reloadPage, setReloadPage] = useState(false);

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return date.toLocaleDateString('es-ES', options);
    };

    useEffect(() => {
        if (reloadPage) {
            setReloadPage(false); // Resetear el estado para evitar recargas infinitas
            window.location.reload();
        }
    }, [reloadPage]);

    const Delete = async (_id) => {
        await deleteProduct(_id);
        setReloadPage(true); // Establecer reloadPage en true para recargar la página
    };

    const closeModal = () => {
        setIsModalOpen(false);
        setReloadPage(true); // Cuando se cierra el modal, se establece reloadPage en true
    };

    return (
        <Columns>
            {products.map(({ nombre_producto, precio, cantidad, local, _id, createdAt }) => (
                <Columns.Column size={4} key={_id}>
                    <Card>
                        <Card.Content>
                            <Content>
                                <Heading>{nombre_producto}</Heading>
                                <Heading subtitle size={6}>Precio: ${precio}</Heading>
                                <Heading subtitle size={6}>Fecha de emisión: {formatDate(createdAt)}</Heading>
                                <p>Local: {local}</p>
                                <p>Cantidad: {cantidad}</p>
                                <EditarButton onClick={() => setIsModalOpen(true)} />
                                <DeleteButton onClick={() => Delete(_id)} />
                            </Content>
                        </Card.Content>
                    </Card>
                </Columns.Column>
            ))}
            <Modal show={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <Modal.Content>
                    <Modal.Card>
                        <Modal.Card.Header showClose={false}>
                            <Modal.Card.Title>
                                Editar Producto
                            </Modal.Card.Title>
                        </Modal.Card.Header>
                        <Modal.Card.Body>
                            {isModalOpen && <Form product={null} closeModal={closeModal} />}
                        </Modal.Card.Body>
                    </Modal.Card>
                </Modal.Content>
            </Modal>
        </Columns>
    );
};

export default ListProduct;
