import React from 'react';
import axios from 'axios';
import { Container, Section, Button } from 'react-bulma-components';

const Correo = () => {
    const handleSendEmail = async () => {
        try {
            const response = await axios.post('http://127.0.0.1:9092/v1/send-email', {
                to: 'jo.almontes@duocuc.cl',
                subject: 'Asunto del correo',
                text: 'Contenido del correo'
            });
            alert('Correo enviado exitosamente');
        } catch (error) {
            console.error('Error al enviar el correo:', error);  // Imprime el error en la consola
            alert('Error al enviar el correo: ' + (error.response?.data || error.message));
        }
    };
    

    return (
        <Section>
            <Container>
                <div className='is-pulled-right'> 
                    <Button onClick={handleSendEmail} color="primary">Enviar Correo</Button>
                </div>
            </Container>
        </Section>
    );
}

export default Correo;
