import React, { useState, useEffect } from 'react';
import { Card, Columns, Content, Heading, Modal } from 'react-bulma-components';
import { deleteProduct, updateProduct} from '../sevices';
import DeleteButton from './Eliminar.js';
import EditarButton from './EditarButton.js';
import Form from "./form";

const ListProduct = ({ products }) => {

    const [isModalOpen , SetIsModalOpen] = useState(false)
    const [reloadPage, setReloadPage] = useState(false); 


    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return date.toLocaleDateString('es-ES', options);
    };

    useEffect(() => {
        if (reloadPage) {
            window.location.reload();
            setReloadPage(false); 
        }
    }, [reloadPage]);

    const Delete = async (_id) => {
        await deleteProduct(_id);
        setReloadPage(true); 
    };
 
    return (
        <Columns>
            {products.map(({ nombre_producto, precio, cantidad, local, _id, createdAt }) => (
                <Columns.Column size={4} key={_id}>
                    <Card >
                        <Card.Content >
                            <Content>
                                <Heading>{nombre_producto}</Heading>
                                <Heading subtitle size={6}> presio: ${precio}</Heading>
                                <Heading subtitle size={6}> Fecha de emision: {formatDate(createdAt)}</Heading>
                                <p>Local: {local}</p>
                                <p>cantidad: {cantidad}</p>
                                <EditarButton onClick={() =>SetIsModalOpen(true)}/>
                                <DeleteButton onClick={() => Delete(_id)} />

                            </Content>
        
                        </Card.Content>
                    </Card>
                </Columns.Column>
            ))}
             <Modal show={isModalOpen} onClose={() => SetIsModalOpen(false) }>
                <Modal.Content>
                    <Modal.Card>
                        <Modal.Card.Header showClose={false}>
                            <Modal.Card.Title >
                                agregar productos
                            </Modal.Card.Title>
                        </Modal.Card.Header>
                        <Modal.Card.Body>
                            <Form /> 
                        </Modal.Card.Body>    
                    </Modal.Card>
                </Modal.Content>    
            </Modal>

        </Columns>
        
    );
}

export default ListProduct;
